import React from "react";
import { Row, Col } from "react-bootstrap";
import "./css/pricing.css";

const Card = (props) => {
    return ( 
        <div className="cards text-white">
            <h1 className="display-6">{props.name}</h1>
            <p>{props.text}</p>

        </div>
     );
}

const Pricing = () => {
    return ( 
        <div className="pricing">
            <p className="blockquote-footer">Lorem ipsum dolor sit amet</p>
            <h1 className="display-4 text-white">Pricing Offers</h1>

            <div className="container mt-4">
                <Row>
                    <Col>
                        <Card name="Basic" text="sit amet lorem et"></Card>
                    </Col>

                    <Col>
                        <Card name="Popular"></Card>
                    </Col>

                    <Col>
                        <Card name="Enterprise"></Card>
                    </Col>
                </Row>
            </div>
        </div>
     );
}

export default Pricing;
